const texts=document.querySelectorAll(".text"),imgs=document.querySelectorAll(".img"),next=document.querySelector(".next"),prev=document.querySelector(".prev"),slide=(e,t)=>{for(let s of texts)s.classList.add("transition"),s.classList.contains("d-none")?(s.style.transform=`translateY(${t}%)`,s.classList.remove("d-none"),setTimeout(()=>{s.style.transform="translateY(0)"},1e3)):(s.style.transform=`translateY(${e}%)`,setTimeout(()=>{s.classList.add("d-none")},1e3));for(let s of imgs)s.classList.add("transition"),s.classList.contains("d-none")?(s.style.transform=`translateX(${t}%)`,s.classList.remove("d-none"),document.body.style.overflowX="hidden",setTimeout(()=>{s.style.transform="translateX(0)"},1e3),setTimeout(()=>document.body.style.overflowX="unset",2e3)):(s.style.transform=`translateX(${e}%)`,setTimeout(()=>{s.classList.add("d-none")},1e3))},slideNext=()=>slide(250,-250),slidePrev=()=>slide(-250,250);next.addEventListener("click",slideNext,!1),prev.addEventListener("click",slidePrev,!1);