const texts=document.querySelectorAll(".text"),imgs=document.querySelectorAll(".img"),next=document.querySelector(".next"),prev=document.querySelector(".prev"),slide=(e,t,s,n)=>{for(let n of texts)n.classList.add("transition"),n.classList.contains("d-none")?(n.style.transform=`translateY(${t}%)`,n.classList.remove("d-none"),setTimeout(()=>{n.style.transform="translateY(0)"},100)):(n.style.transform=`translateY(${e}%)`,setTimeout(()=>{n.classList.add("d-none")},s));for(let o of imgs)o.classList.add("transition"),o.classList.contains("d-none")?(o.style.transform=`translateX(${t}%)`,o.classList.remove("d-none"),setTimeout(()=>{o.style.transform="translateX(0)"},100),setTimeout(()=>document.body.style.overflowX="unset",n)):(document.body.style.overflowX="hidden",o.style.transform=`translateX(${e}%)`,setTimeout(()=>{o.classList.add("d-none")},s))},slideNext=()=>slide(250,-250,300,300),slidePrev=()=>slide(-250,250,700,1e3);next.addEventListener("click",slideNext,!1),prev.addEventListener("click",slidePrev,!1);